mapper.select:
select usr_id, email, name, tel, auth, cdt from usr u left outer join auth a on u.auth_id=a.auth_id
name like #{pattern1} or email like #{pattern1} or tel like #{pattern1}
order by usr_id desc
limit #{rowNo}, #{size}

select
 count(*) 
from
 usr



    name like #{pattern1}
    or email like #{pattern1}
    or tel like #{pattern1}

mapper.update:
update usr
name = #{name}, email = #{email}, pwd = password(#{password}), tel = #{tel}, auth_id = #{auth}, usr_photo = #{photo}
where usr_id = #{no}
